discovery_rule.params:
NETS=($(grep : /proc/net/dev | grep -v lo: | sed 's|:.*$||g'))
data=$(
    for NET in "${NETS[@]}"; { printf "
	{ "{#IFNAME}":"$NET"	},"; }
    printf '_LAST_COMMA_'
)
printf "{
	"data":[
${data//,_LAST_COMMA_/\n}
	]
}
"

IFSOLD=$IFS; IFS=$'
'; FSS=($(df -Tl -xtmpfs -xdevtmpfs)); IFS=$IFSOLD
data=$(
    for FS in "${FSS[@]:1}"; {
        fs=($FS); printf "
	{ "{#FSNAME}":"${fs[-1]}",		"{#FSTYPE}":"${fs[1]}"	},"
    }; printf '_LAST_COMMA_'
)
printf "{
	"data":[
${data//,_LAST_COMMA_/\n}
	]
}
"
